import sensor_lib as slimport settingsimport sched# get the pinpin = settings.GPIO_PORTserial = settings.SENSOR_SERIALinterval = settings.READING_INTERVALunit = os.getenv('UNIT',None)serial = sl.generate_serial()if(str(unit) is None):    unit = 'F'    u = 'fahrenheit'if(str(unit).lower() == 'f' or str(unit).lower() == 'fahrenheit' ):    unit = 'F'    u = 'fahrenheit'if(str(unit).lower() == 'c' or str(unit).lower() == 'celsius' ):    unit = 'C'    u = 'celsius'token = sl.get_backend_token()try:    humidity, temperature = Adafruit_DHT.read_retry(11,int(pin))    if(unit == 'F'):        temperature = temperature * 9/5.0 + 32    if humidity is not None and temperature is not None:        sl.send_reading( { 'reading':temperature,                                     'reading_type':'temp',                                     'reading_unit':u,                                     'sensor_serial':serial,                                     'token':token['token']                                    } )except Exception as e:    raise ('Could not get data from DHT11 sensor. %s')%(e)